import{_ as i,c as e,o as n,ah as o,ai as t,aj as l,ak as r}from"./chunks/framework.D43GgaY6.js";const b=JSON.parse('{"title":"Podešavanja stambenih zajednica","description":"Uputstvo za podešavanje podataka o stambenim zajednicama, uređivanje nekretnina i upravljanje oglasnom tablom u aplikaciji Mileva.","frontmatter":{"title":"Podešavanja stambenih zajednica","description":"Uputstvo za podešavanje podataka o stambenim zajednicama, uređivanje nekretnina i upravljanje oglasnom tablom u aplikaciji Mileva."},"headers":[],"relativePath":"building.md","filePath":"building.md"}'),d={name:"building.md"};function s(p,a,m,j,c,v){return n(),e("div",null,[...a[0]||(a[0]=[o('<h1 id="podesavanja-stambenih-zajednica" tabindex="-1">Podešavanja stambenih zajednica <a class="header-anchor" href="#podesavanja-stambenih-zajednica" aria-label="Permalink to “Podešavanja stambenih zajednica”">​</a></h1><p>Ova stranica objašnjava kako da pristupite podešavanjima stambenih zajednica, kako da izmenite osnovne podatke o zajednici, uredite sve pripadajuće nekretnine i upravljate oglasnom tablom.</p><h2 id="pristup-podesavanjima" tabindex="-1">Pristup podešavanjima <a class="header-anchor" href="#pristup-podesavanjima" aria-label="Permalink to “Pristup podešavanjima”">​</a></h2><ol><li>Kliknite na svoju profilnu sliku u gornjem desnom uglu aplikacije.</li><li>Izaberite opciju Podešavanja.</li><li>U levom bočnom meniju odaberite stavku Stambene zajednice.</li></ol><figure style="text-align:center;margin:1.5rem auto;max-width:900px;width:100%;"><img src="'+t+'" alt="Pregled stambenih zajednica i osnovna podešavanja" style="display:block;margin:0 auto;max-width:900px;width:100%;border:1px solid #e5e7eb;border-radius:8px;"><figcaption style="margin-top:.5rem;color:#666;">Pregled stambenih zajednica i osnovna podešavanja</figcaption></figure><h2 id="_1-osnovni-podaci-o-stambenoj-zajednici" tabindex="-1">1. Osnovni podaci o stambenoj zajednici <a class="header-anchor" href="#_1-osnovni-podaci-o-stambenoj-zajednici" aria-label="Permalink to “1. Osnovni podaci o stambenoj zajednici”">​</a></h2><figure style="text-align:center;margin:1.5rem auto;max-width:900px;width:100%;"><img src="'+l+'" alt="Uređivanje nekretnina u okviru stambene zajednice" style="display:block;margin:0 auto;max-width:900px;width:100%;border:1px solid #e5e7eb;border-radius:8px;"><figcaption style="margin-top:.5rem;color:#666;">Uređivanje nekretnina u okviru stambene zajednice</figcaption></figure><p>Klikom na ime željene stambene zajednice otvara se dodatni prozor sa detaljima. U ovom delu možete izmeniti ključne podatke kao što su:</p><ul><li>Adresa (ulica i broj)</li><li>Grad i opština</li><li>Poštanski broj</li><li>Matični broj</li><li>PIB</li><li>Ostali identifikacioni i kontakt podaci po potrebi</li></ul><p>Promene sačuvajte klikom na dugme Izmeni.</p><h2 id="_2-uređivanje-nekretnina" tabindex="-1">2. Uređivanje nekretnina <a class="header-anchor" href="#_2-uređivanje-nekretnina" aria-label="Permalink to “2. Uređivanje nekretnina”">​</a></h2><p>Odeljak Uredi nekretnine omogućava upravljanje svim sastavnim delovima stambene zajednice. Tu spadaju:</p><ul><li>Stanovi</li><li>Parking mesta</li><li>Garaže i garažni boksovi</li><li>Komercijalni/poslovni prostori</li><li>Ostale jedinice i zajedničke prostorije</li></ul><p>Klikom na Uredi nekretnine otvara se pregled sa zbirnim brojem dodeljenih jedinica (npr. broj stanova, parking mesta, poslovnih prostora). Iz dodatnog menija možete izabrati bilo koju stavku i otvoriti dijalog za uređivanje detalja svake pojedinačne jedinice.</p><h2 id="_3-oglasna-tabla" tabindex="-1">3. Oglasna tabla <a class="header-anchor" href="#_3-oglasna-tabla" aria-label="Permalink to “3. Oglasna tabla”">​</a></h2><p>U odeljku Oglasna tabla možete definisati i objavljivati informacije koje će biti vidljive svim stanarima povezanim sa izabranom stambenom zajednicom.</p><ul><li>Otvorite podešavanja stambene zajednice i kliknite na Oglasna tabla.</li><li>U dijalogu su prikazane osnovne informacije o svrsi oglasne table i polje za unos sadržaja poruke.</li><li>Dostupan je i deo za prilaganje prateće dokumentacije.</li></ul><p>Podržani tipovi i ograničenja priloga:</p><ul><li>Do 5 fotografija (JPEG, PNG, WebP), do 5 MB po datoteci</li><li>Do 2 video zapisa (MP4), do 50 MB po datoteci</li><li>Do 3 dokumenta (PDF, DOC/DOCX, XLS/XLSX), do 10 MB po datoteci</li></ul><figure style="text-align:center;margin:1.5rem auto;max-width:900px;width:100%;"><img src="'+r+'" alt="Podešavanja i objave na oglasnoj tabli" style="display:block;margin:0 auto;max-width:900px;width:100%;border:1px solid #e5e7eb;border-radius:8px;"><figcaption style="margin-top:.5rem;color:#666;">Podešavanja i objave na oglasnoj tabli</figcaption></figure><div class="tip custom-block"><p class="custom-block-title">Napomena</p><p>Ovde možete navesti važne informacije o zgradi (pravila, kontakte za hitne slučajeve, priložiti dokumenta itd.). Ove informacije biće zakačene na glavni ekran aplikacije za stanare koji su povezani sa ovom zgradom.</p></div>',21)])])}const k=i(d,[["render",s]]);export{b as __pageData,k as default};
