import{_ as e,c as i,o as n,ah as o,al as r,am as t}from"./chunks/framework.D43GgaY6.js";const j=JSON.parse('{"title":"Chat","description":"Razmena tekstualnih poruka u realnom vremenu između stanara. Obaveštenja, prilozi uz poruke i glasovne poruke. Upravnik može da isključi funkcionalnost za celu zajednicu.","frontmatter":{"title":"Chat","description":"Razmena tekstualnih poruka u realnom vremenu između stanara. Obaveštenja, prilozi uz poruke i glasovne poruke. Upravnik može da isključi funkcionalnost za celu zajednicu.","outline":[2,3]},"headers":[],"relativePath":"chat.md","filePath":"chat.md"}'),s={name:"chat.md"};function l(u,a,k,m,p,c){return n(),i("div",null,[...a[0]||(a[0]=[o('<figure style="text-align:center;margin:2rem auto;max-width:680px;width:100%;"><img src="'+r+'" alt="Chat — razmena poruka u realnom vremenu" style="display:block;margin:0 auto;max-width:680px;width:100%;"></figure><h1 id="chat-—-razmena-poruka-u-realnom-vremenu" tabindex="-1">Chat — razmena poruka u realnom vremenu <a class="header-anchor" href="#chat-—-razmena-poruka-u-realnom-vremenu" aria-label="Permalink to “Chat — razmena poruka u realnom vremenu”">​</a></h1><p>Chat omogućava stanarima da međusobno komuniciraju kratkim tekstualnim porukama u realnom vremenu. Namenjen je brzoj razmeni informacija koje se tiču upravljanja stambenom zajednicom i svakodnevnih operativnih pitanja.</p><h2 id="osnovne-karakteristike" tabindex="-1">Osnovne karakteristike <a class="header-anchor" href="#osnovne-karakteristike" aria-label="Permalink to “Osnovne karakteristike”">​</a></h2><ul><li>Razmena poruka u realnom vremenu između stanara povezane zgrade.</li><li>Automatska obaveštenja: svi registrovani korisnici dobijaju obaveštenje kada je poslata nova poruka.</li><li>Prilozi uz poruke: moguć je upload dokumentacije (npr. zapisnici, slike računa, ponude) kao atačmenta uz poruku.</li><li>Glasovne poruke: snimanje i slanje glasovnih poruka preko ikone mikrofona sa desne strane input polja.</li></ul><figure style="text-align:center;margin:1rem 0;"><img src="'+t+'" alt="Glavni prikaz razgovora — lista poruka, polje za unos, ikona mikrofona i dodavanje priloga" style="max-width:960px;width:100%;border-radius:8px;box-shadow:0 6px 24px rgba(0,0,0,.08);"><figcaption style="margin-top:.5rem;color:#666;">Glavni prikaz chata</figcaption></figure><h2 id="ukljucivanje-iskljucivanje-funkcionalnosti" tabindex="-1">Uključivanje/isključivanje funkcionalnosti <a class="header-anchor" href="#ukljucivanje-iskljucivanje-funkcionalnosti" aria-label="Permalink to “Uključivanje/isključivanje funkcionalnosti”">​</a></h2><p>Chat je opciona funkcionalnost. Upravnik stambene zajednice može u bilo kom trenutku da isključi chat za celu zajednicu ukoliko proceni da je to potrebno. Funkcionalnost chata nije krucijalna za rad aplikacije — predstavlja dodatni kanal komunikacije.</p><div class="info custom-block"><p class="custom-block-title">Važno</p><p>Svi korisnici koji su registrovani i povezani sa zgradom automatski su uključeni u chat sobu. Upravnik ne može pojedinačno da isključi ili ukloni nekog stanara iz sobe.</p></div><h2 id="obavestenja-i-dostupnost" tabindex="-1">Obaveštenja i dostupnost <a class="header-anchor" href="#obavestenja-i-dostupnost" aria-label="Permalink to “Obaveštenja i dostupnost”">​</a></h2><ul><li>Kada se objavi nova poruka na chatu, registrovani članovi dobijaju obaveštenje o novoj aktivnosti.</li><li>Obaveštenja omogućavaju da se važne informacije brzo podele i da se stanari efikasno usaglase oko praktičnih pitanja (npr. termin održavanja sednice, kratki dogovori oko održavanja i sl.).</li></ul><h2 id="pravila-koriscenja" tabindex="-1">Pravila korišćenja <a class="header-anchor" href="#pravila-koriscenja" aria-label="Permalink to “Pravila korišćenja”">​</a></h2><p>Chat je namenjen isključivo temama koje su u vezi sa funkcionisanjem i upravljanjem stambenom zajednicom. Molimo da komunikacija bude sažeta, jasna i fokusirana na predmetne teme.</p><div class="tip custom-block"><p class="custom-block-title">Savet</p><p>Koristite chat za brze koordinacije (npr. termin sednice, kratka obaveštenja), dok se za zvanične odluke i formalnu dokumentaciju i dalje koristi eSednica i oglasna tabla.</p></div>',14)])])}const v=e(s,[["render",l]]);export{j as __pageData,v as default};
