import{_ as e,c as i,o as n,ah as o,an as t,ao as r,ap as s,aq as l,ar as d}from"./chunks/framework.Q_8nPqYE.js";const h=JSON.parse('{"title":"Odluke SZ","description":"","frontmatter":{"title":"Odluke SZ"},"headers":[],"relativePath":"decisions.md","filePath":"decisions.md"}'),u={name:"decisions.md"};function k(j,a,p,m,g,c){return n(),i("div",null,[...a[0]||(a[0]=[o('<figure style="text-align:center;margin:2rem auto;max-width:680px;width:100%;"><img src="'+t+'" alt="Odluke stambene zajednice – zaključivanje glasanja i pregled odluka" style="display:block;margin:0 auto;max-width:680px;width:100%;"></figure><h1 id="odluke-stambene-zajednice" tabindex="-1">Odluke stambene zajednice <a class="header-anchor" href="#odluke-stambene-zajednice" aria-label="Permalink to “Odluke stambene zajednice”">​</a></h1><h2 id="kada-se-zatvara-elektronsko-glasanje" tabindex="-1">Kada se zatvara elektronsko glasanje <a class="header-anchor" href="#kada-se-zatvara-elektronsko-glasanje" aria-label="Permalink to “Kada se zatvara elektronsko glasanje”">​</a></h2><p>Nakon što istekne vreme predviđeno za održavanje sednice skupštine stanara – ili kada upravnik proceni da su se svi obavezni učesnici izjasnili – u odeljku eSednica bira opciju „Zaustavi i dodaj offline glasove“. Ovom akcijom se formalno prekida elektronsko glasanje i od tog trenutka više nije moguće glasati putem aplikacije.</p><h3 id="zaustavljanje-glasanja-u-okviru-esednice" tabindex="-1">Zaustavljanje glasanja (u okviru eSednice) <a class="header-anchor" href="#zaustavljanje-glasanja-u-okviru-esednice" aria-label="Permalink to “Zaustavljanje glasanja (u okviru eSednice)”">​</a></h3><figure style="text-align:center;margin:1.5rem auto;max-width:900px;width:100%;"><img src="'+r+'" alt="Prikaz liste tačaka i dugmeta ‘Zaustavi i dodaj offline glasove’ u odeljku E7" style="display:block;margin:0 auto;max-width:900px;width:100%;border:1px solid #e5e7eb;border-radius:8px;"><figcaption style="margin-top:.5rem;color:#666;">Dugme za zaustavljanje glasanja i prelazak na unos offline glasova</figcaption></figure><figure style="text-align:center;margin:1.25rem auto;max-width:720px;width:100%;"><img src="'+s+'" alt="Dijalog za potvrdu zaustavljanja glasanja" style="display:block;margin:0 auto;max-width:720px;width:100%;border:1px solid #e5e7eb;border-radius:8px;"><figcaption style="margin-top:.5rem;color:#666;">Potvrda zaustavljanja glasanja</figcaption></figure><h2 id="dodavanje-offline-glasova" tabindex="-1">Dodavanje offline glasova <a class="header-anchor" href="#dodavanje-offline-glasova" aria-label="Permalink to “Dodavanje offline glasova”">​</a></h2><p>Za stanare koji se još nisu registrovali u aplikaciji, upravnik može uneti kako su glasali van sistema (offline). Unos se vrši za svakog stanara pojedinačno i za svako pitanje sa dnevnog reda, uz izbor jedne od opcija: „Za“, „Protiv“ ili „Uzdržan“.</p><figure style="text-align:center;margin:1.5rem auto;max-width:900px;width:100%;"><img src="'+l+'" alt="Forma za unos offline glasova po svakoj tački dnevnog reda" style="display:block;margin:0 auto;max-width:900px;width:100%;border:1px solid #e5e7eb;border-radius:8px;"><figcaption style="margin-top:.5rem;color:#666;">Unos offline glasova po stanaru i po tački dnevnog reda</figcaption></figure><h2 id="zakljucavanje-sednice-i-statistika-glasanja" tabindex="-1">Zaključavanje sednice i statistika glasanja <a class="header-anchor" href="#zakljucavanje-sednice-i-statistika-glasanja" aria-label="Permalink to “Zaključavanje sednice i statistika glasanja”">​</a></h2><p>Kada su svi offline glasovi uneti, sednica se zaključava. Tada postaje dostupna statistika glasanja – pregled po svakoj tački dnevnog reda, uz transparentan uvid u način glasanja učesnika. Statistika je vidljiva i u detaljima svake pojedinačne sednice i dostupna je upravnicima kao i stanarima koji su učestvovali u glasanju.</p><h2 id="pregled-i-preuzimanje-odluka" tabindex="-1">Pregled i preuzimanje odluka <a class="header-anchor" href="#pregled-i-preuzimanje-odluka" aria-label="Permalink to “Pregled i preuzimanje odluka”">​</a></h2><p>U delu aplikacije koji se odnosi na odluke „Odluke SZ“ sa desne strane prikazuju se rezultati svih održanih sednica. Za svaku sednicu generiše se jedan PDF dokument sa zakonski propisanim, bitnim elementima – u formi odluke ili izveštaja o glasanju – koji može da posluži kao zvanična dokumentacija.</p><figure style="text-align:center;margin:1.5rem auto;max-width:900px;width:100%;"><img src="'+d+'" alt="Primer izveštaja o glasanju sa statistikom po tačkama dnevnog reda" style="display:block;margin:0 auto;max-width:900px;width:100%;border:1px solid #e5e7eb;border-radius:8px;"><figcaption style="margin-top:.5rem;color:#666;">Primer izveštaja/odluke sa statistikom glasanja</figcaption></figure><h3 id="preuzimanje-i-prilagođavanje-pdf-a" tabindex="-1">Preuzimanje i prilagođavanje PDF-a <a class="header-anchor" href="#preuzimanje-i-prilagođavanje-pdf-a" aria-label="Permalink to “Preuzimanje i prilagođavanje PDF-a”">​</a></h3><ul><li>Odluke se mogu preuzeti uvek i u svakom trenutku u PDF formatu.</li><li>Forma dokumenta može se prilagoditi svakoj stambenoj zajednici u skladu sa potrebama.</li><li>Dokument sadrži deo za potpis upravnika stambene zajednice, čime se formalizuje odluka ili rezultat glasanja po svakoj tački dnevnog reda.</li></ul><h3 id="pretraga-i-sortiranje-odluka" tabindex="-1">Pretraga i sortiranje odluka <a class="header-anchor" href="#pretraga-i-sortiranje-odluka" aria-label="Permalink to “Pretraga i sortiranje odluka”">​</a></h3><ul><li>Rezultate/odluke možete sortirati po više kriterijuma radi bržeg pronalaženja.</li><li>Uvek je vidljiv datum održavanja sednice, što olakšava pronalaženje konkretne odluke.</li></ul>',19)])])}const f=e(u,[["render",k]]);export{h as __pageData,f as default};
