import{_ as i,c as e,o as r,ah as t,aE as o,aF as n,aG as s,aH as l,aI as v}from"./chunks/framework.Q_8nPqYE.js";const c=JSON.parse('{"title":"Izveštaji","description":"Prijava problema u zgradi, statusi izveštaja, komentarisanje i završavanje — uputstvo za stanare i upravnike.","frontmatter":{"title":"Izveštaji","description":"Prijava problema u zgradi, statusi izveštaja, komentarisanje i završavanje — uputstvo za stanare i upravnike."},"headers":[],"relativePath":"reports.md","filePath":"reports.md"}'),p={name:"reports.md"};function j(d,a,m,u,k,z){return r(),e("div",null,[...a[0]||(a[0]=[t('<figure style="text-align:center;margin:2rem auto;max-width:680px;width:100%;"><img src="'+o+'" alt="Izveštaji — baner" style="display:block;margin:0 auto;max-width:680px;width:100%;"></figure><h1 id="izvestaji" tabindex="-1">Izveštaji <a class="header-anchor" href="#izvestaji" aria-label="Permalink to “Izveštaji”">​</a></h1><h2 id="svrha-i-pregled" tabindex="-1">Svrha i pregled <a class="header-anchor" href="#svrha-i-pregled" aria-label="Permalink to “Svrha i pregled”">​</a></h2><p>Izveštaji služe da stanari i upravnik prijave problem u zgradi i prate njegovo rešavanje. Na glavnom ekranu se nalazi lista svih izveštaja sa mogućnošću filtriranja po statusu i pretragom. Vidljivost izveštaja za sve stanare zavisi od validacije koju obavlja upravnik.</p><figure style="text-align:center;margin:1rem 0;"><img src="'+n+'" alt="Pregled izveštaja sa statusima i filtrima" style="max-width:960px;width:100%;border-radius:8px;box-shadow:0 6px 24px rgba(0,0,0,.08);"><figcaption style="margin-top:.5rem;color:#666;">Pregled izveštaja sa statusima i filtrima</figcaption></figure><h2 id="statusi-izvestaja-i-vidljivost" tabindex="-1">Statusi izveštaja i vidljivost <a class="header-anchor" href="#statusi-izvestaja-i-vidljivost" aria-label="Permalink to “Statusi izveštaja i vidljivost”">​</a></h2><p>Izveštaj može imati jedan od sledećih statusa:</p><ul><li>Na čekanju — početni status kada izveštaj kreira stanar. Upravnik proverava prikladnost sadržaja i odlučuje da li izveštaj postaje vidljiv svim stanarima.</li><li>U procesu — izveštaj je validiran i rešavanje je u toku. Izveštaj je vidljiv svim stanarima povezanim sa odgovarajućom zgradom.</li><li>Završeno — problem je rešen. Upravnik unosi kratak opis rešenja i opciono dodaje priloge.</li></ul><p>Upravnik može filtrirati izveštaje po statusu i odlučiti da li pojedini izveštaji treba da budu javno vidljivi stanarima (kada su relevantni) ili da ostanu nevidljivi (ako sadržaj nije primeren).</p><h2 id="kreiranje-novog-izvestaja-prijava-problema" tabindex="-1">Kreiranje novog izveštaja (prijava problema) <a class="header-anchor" href="#kreiranje-novog-izvestaja-prijava-problema" aria-label="Permalink to “Kreiranje novog izveštaja (prijava problema)”">​</a></h2><ol><li>U levom meniju izaberite Izveštaji.</li><li>Kliknite na dugme Prijavi problem.</li><li>U dijalogu unesite: <ul><li>Naslov izveštaja — kratak, jasan naziv.</li><li>Opis — sažeto objasnite u čemu je problem i gde se nalazi.</li><li>Prilozi (opciono) — fotografije, video zapisi ili dokumenti.</li></ul></li></ol><p>Ograničenja priloga i unosa:</p><ul><li>Naslov i opis imaju ograničen broj karaktera kako bi sadržaj bio jasan i koncizan.</li><li>Do 5 fotografija, do 2 video zapisa i do 3 dokumenta.</li><li>Maksimalna veličina je 10 MB po datoteci.</li></ul><p>Nakon slanja, izveštaj prvo vidi upravnik koji ga validira i menja status u U procesu kada započne rešavanje. Tada izveštaj postaje vidljiv svim stanarima iz odgovarajuće zgrade.</p><figure style="text-align:center;margin:1rem 0;"><img src="'+s+'" alt="Kreiranje novog izveštaja — dijalog za unos naslova, opisa i priloga" style="max-width:960px;width:100%;border-radius:8px;box-shadow:0 6px 24px rgba(0,0,0,.08);"><figcaption style="margin-top:.5rem;color:#666;">Kreiranje novog izveštaja</figcaption></figure><div class="tip custom-block"><p class="custom-block-title">Savet</p><p>Držite naslov i opis što kraćim i preciznim. Fokusirajte se na lokaciju, kratak opis problema i eventualno kontakt.</p></div><h2 id="komentarisanje-izvestaja" tabindex="-1">Komentarisanje izveštaja <a class="header-anchor" href="#komentarisanje-izvestaja" aria-label="Permalink to “Komentarisanje izveštaja”">​</a></h2><ul><li>Dok je izveštaj u statusu U procesu, svi stanari mogu dodavati komentare kako bi dopunili informacije ili potvrdili zapažanja.</li><li>Kada upravnik promeni status u Završeno i doda završni komentar, samo upravnik može ostaviti dodatne napomene na već završen izveštaj.</li></ul><p>Komentari su jasno povezani sa konkretnim izveštajem i vidljivi su u njegovom detaljnom prikazu.</p><h2 id="zavrsavanje-izvestaja" tabindex="-1">Završavanje izveštaja <a class="header-anchor" href="#zavrsavanje-izvestaja" aria-label="Permalink to “Završavanje izveštaja”">​</a></h2><p>Kada je problem rešen, upravnik u polju Status bira opciju Završeno. Otvara se dijalog u koji unosi kratak opis načina rešavanja i, po potrebi, dodaje fotografije, video zapise ili dokumenta kao dokaz.</p><figure style="text-align:center;margin:1rem 0;"><img src="'+l+'" alt="Označavanje izveštaja kao završen — unos završnog opisa i priloga" style="max-width:960px;width:100%;border-radius:8px;box-shadow:0 6px 24px rgba(0,0,0,.08);"><figcaption style="margin-top:.5rem;color:#666;">Označavanje izveštaja kao završen</figcaption></figure><figure style="text-align:center;margin:1rem 0;"><img src="'+v+'" alt="Pregled završenog izveštaja sa vidljivim završnim komentarom" style="max-width:960px;width:100%;border-radius:8px;box-shadow:0 6px 24px rgba(0,0,0,.08);"><figcaption style="margin-top:.5rem;color:#666;">Pregled završenog izveštaja i završni komentar</figcaption></figure><h2 id="izvestaji-koje-kreira-upravnik" tabindex="-1">Izveštaji koje kreira upravnik <a class="header-anchor" href="#izvestaji-koje-kreira-upravnik" aria-label="Permalink to “Izveštaji koje kreira upravnik”">​</a></h2><p>Upravnik takođe može prijaviti problem. U tom slučaju izveštaj odmah dobija status U procesu (nema dodatne validacije), jer je upravnik već potvrdio relevantnost i započeo rešavanje.</p><h2 id="dodatne-napomene" tabindex="-1">Dodatne napomene <a class="header-anchor" href="#dodatne-napomene" aria-label="Permalink to “Dodatne napomene”">​</a></h2><ul><li>Izbegavajte neprimeren sadržaj i nepotrebne komentare — cilj je brzo i efikasno rešavanje problema.</li><li>Za izveštaje koji se ne tiču svih, upravnik može ograničiti vidljivost.</li></ul><h2 id="pomoc" tabindex="-1">Pomoć <a class="header-anchor" href="#pomoc" aria-label="Permalink to “Pomoć”">​</a></h2><p>Ako imate nedoumica, obratite se upravniku zgrade ili timu podrške aplikacije.</p>',29)])])}const h=i(p,[["render",j]]);export{c as __pageData,h as default};
