import{_ as e,c as i,o as n,ah as t,aG as r,aH as o,aI as s}from"./chunks/framework.B8aMTWxH.js";const v=JSON.parse('{"title":"eSednica","description":"Održavanje elektronske sednice skupštine stanara — planiranje, pitanja, glasanje","frontmatter":{"title":"eSednica","description":"Održavanje elektronske sednice skupštine stanara — planiranje, pitanja, glasanje","outline":[2,3]},"headers":[],"relativePath":"sednica.md","filePath":"sednica.md"}'),l={name:"sednica.md"};function d(p,a,j,u,c,m){return n(),i("div",null,[...a[0]||(a[0]=[t('<figure style="text-align:center;margin:2rem auto;max-width:680px;width:100%;"><img src="'+r+'" alt="eSednica — glasanje (DA, NE, UZDRŽAN) — baner" style="display:block;margin:0 auto;max-width:680px;width:100%;"></figure><h1 id="esednica-—-elektronska-sednica-skupstine-stanara" tabindex="-1">eSednica — elektronska sednica skupštine stanara <a class="header-anchor" href="#esednica-—-elektronska-sednica-skupstine-stanara" aria-label="Permalink to “eSednica — elektronska sednica skupštine stanara”">​</a></h1><p>Elektronska sednica („eSednica“) omogućava da se svi članovi skupštine stanara izjasne o važnim pitanjima koja se tiču stambene zajednice, bez potrebe za fizičkim prisustvom. Upravnik može da isplanira sednicu, doda teme i materijale, a stanari glasaju u predviđenom vremenskom okviru.</p><h2 id="brzi-pregled" tabindex="-1">Brzi pregled <a class="header-anchor" href="#brzi-pregled" aria-label="Permalink to “Brzi pregled”">​</a></h2><ul><li>Pregled aktivnih i završenih sednica na jednom mestu.</li><li>Planiranje nove sednice kroz vođeni dijalog (razlog, datum i vreme, pitanja, prilozi).</li><li>Mogućnost izmene svih detalja sednice od strane upravnika do početka glasanja.</li><li>Jasno definisano glasanje za stanare: ZA, PROTIV (NE) ili UZDRŽAN.</li></ul><figure style="text-align:center;margin:1rem 0;"><img src="'+o+'" alt="Pregled eSednica — aktivne i završene sednice sa osnovnim podacima" style="max-width:960px;width:100%;border-radius:8px;box-shadow:0 6px 24px rgba(0,0,0,.08);"><figcaption style="margin-top:.5rem;color:#666;">Pregled sednica — aktivne i završene</figcaption></figure><h2 id="pokretanje-nove-sednice" tabindex="-1">Pokretanje nove sednice <a class="header-anchor" href="#pokretanje-nove-sednice" aria-label="Permalink to “Pokretanje nove sednice”">​</a></h2><ol><li>U levom meniju izaberite eSednica.</li><li>Kliknite na dugme Planiraj novi sastanak.</li><li>U dijalogu unesite sledeće informacije: <ul><li>Razlog sazivanja — kratko objasnite zbog čega se zakazuje sednica.</li><li>Datum i vreme — odredite termin početka sednice i period za glasanje.</li><li>Dokumenta/prilozi — opcionalno dodajte ponude, zapisnike i druge materijale za uvid članovima.</li><li>Pitanja i tip pitanja — formirajte dnevni red i izaberite tip za svako pitanje (tip utiče na način obračuna kvoruma i potrebne većine).</li></ul></li><li>Sačuvajte i objavite sednicu.</li></ol><figure style="text-align:center;margin:1rem 0;"><img src="'+s+'" alt="Dijalog za planiranje nove eSednice sa poljima: razlog, datum i vreme, prilozi i pitanja" style="max-width:960px;width:100%;border-radius:8px;box-shadow:0 6px 24px rgba(0,0,0,.08);"><figcaption style="margin-top:.5rem;color:#666;">Planiranje nove sednice</figcaption></figure><h2 id="pitanja-i-tipovi-pitanja" tabindex="-1">Pitanja i tipovi pitanja <a class="header-anchor" href="#pitanja-i-tipovi-pitanja" aria-label="Permalink to “Pitanja i tipovi pitanja”">​</a></h2><p>Tip pitanja određuje pravila obračuna i izračunavanja potrebnog kvoruma. Zbog toga je važno da za svaku tačku dnevnog reda odaberete adekvatan tip, u skladu sa propisima i praksom stambene zajednice.</p><div class="tip custom-block"><p class="custom-block-title">Savet</p><p>Formulišite svako pitanje kao jasnu odluku o kojoj se može glasati: odgovori treba da budu „ZA“, „PROTIV (NE)“ ili „UZDRŽAN“. Na taj način, rezultati će biti merljivi i nedvosmisleni.</p></div><h2 id="izmena-detalja-sednice" tabindex="-1">Izmena detalja sednice <a class="header-anchor" href="#izmena-detalja-sednice" aria-label="Permalink to “Izmena detalja sednice”">​</a></h2><p>Nakon kreiranja sednice, upravnik može izmeniti sve detalje (opis/razlog, termin, tačke dnevnog reda, priloge), posebno u situacijama kada je potrebno dodati novu tačku o kojoj se stanari izjašnjavaju. Izmene su moguće do početka glasanja, u skladu sa pravilima stambene zajednice.</p><h2 id="obavestenja-i-glasanje" tabindex="-1">Obaveštenja i glasanje <a class="header-anchor" href="#obavestenja-i-glasanje" aria-label="Permalink to “Obaveštenja i glasanje”">​</a></h2><ul><li>Svi registrovani stanari dobijaju obaveštenje kada je sednica kreirana i zakazana.</li><li>Početkom sednice, stiže dodatno obaveštenje da je glasanje otvoreno i dostupno.</li><li>Stanari pristupaju sednici i glasaju za svaku stavku dnevnog reda birajući: ZA, PROTIV (NE) ili UZDRŽAN.</li></ul><h2 id="ko-moze-da-kreira-sednicu" tabindex="-1">Ko može da kreira sednicu <a class="header-anchor" href="#ko-moze-da-kreira-sednicu" aria-label="Permalink to “Ko može da kreira sednicu”">​</a></h2><p>Kreiranje eSednice dostupno je isključivo upravniku stambene zajednice. Stanari učestvuju u procesu kroz pregled materijala i elektronsko glasanje u predviđenom roku.</p>',18)])])}const g=e(l,[["render",d]]);export{v as __pageData,g as default};
